import React from 'react';
import { motion } from 'framer-motion';
import { Play, Users } from 'lucide-react';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.querySelector(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center bg-bw-black">
      <div className="container-custom section-padding relative z-10 text-center flex flex-col items-center justify-center">
        {/* FF Logo above title, less margin */}
        <div className="flex flex-col items-center" style={{ marginTop: '1.5rem', marginBottom: '1.5rem' }}>
          <img
            src={process.env.PUBLIC_URL + '/FF_logo.png'}
            alt="FF Logo"
            className="mx-auto w-56 md:w-80 h-auto object-contain select-none"
          />
        </div>
        <span className="font-anton text-5xl md:text-7xl font-bold uppercase text-bw-white tracking-wider select-none mb-6">
          FELIX FALKEN
        </span>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2 }}
          className="space-y-8"
        >
          {/* Main Title */}
          <motion.h1
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 1, delay: 0.4 }}
            className="text-4xl md:text-6xl font-anton tracking-wider text-bw-white mb-2"
          >
            ELECTRONIC SOUND ARCHITECT
          </motion.h1>

          {/* Description */}
          <motion.p
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.8 }}
            className="text-lg md:text-xl text-bw-white/80 max-w-2xl mx-auto font-montserrat mb-8"
          >
            Crafting immersive synth-driven sounds that transport you to digital realms
          </motion.p>

          {/* CTA Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 1 }}
            className="flex flex-col sm:flex-row gap-6 justify-center items-center pt-4"
          >
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#music')}
              className="bw-button group"
            >
              <Play className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
              Listen Now
            </motion.button>

            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#collaborate')}
              className="bw-button group"
            >
              <Users className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
              Collaborate
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
