import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { Play, Users } from 'lucide-react';
import VANTA from 'vanta/dist/vanta.net.min';

const Hero = () => {
  const [vantaEffect, setVantaEffect] = useState(null);
  const vantaRef = useRef(null);

  useEffect(() => {
    if (!vantaEffect) {
      setVantaEffect(
        VANTA({
          el: vantaRef.current,
          color: 0xffffff,
          backgroundColor: 0x000000,
          mouseControls: true,
          touchControls: true,
          gyroControls: false,
          minHeight: 100.00,
          minWidth: 100.00,
          scale: 1.00,
          scaleMobile: 1.00,
          points: 25.00,
          maxDistance: 35.00,
          spacing: 15.00,
          showLines: true,
          lineColor: 0x00ffff,
          lineWidth: 1.5,
          pointSize: 2.5,
          pointColor: 0xffffff
        })
      );
    }
    return () => {
      if (vantaEffect) vantaEffect.destroy();
    };
  }, [vantaEffect]);

  const scrollToSection = (sectionId) => {
    const element = document.querySelector(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Vanta.js Animated Background */}
      <div ref={vantaRef} className="absolute inset-0 z-0" />

      {/* Scanlines Overlay */}
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white/5 to-transparent opacity-10 pointer-events-none z-10" 
           style={{
             backgroundImage: `repeating-linear-gradient(
               0deg,
               transparent,
               transparent 2px,
               rgba(255,255,255,0.03) 2px,
               rgba(255,255,255,0.03) 4px
             )`
           }} />

      {/* Subtle Grid Pattern */}
      <div className="absolute inset-0 pointer-events-none z-5 opacity-5"
           style={{
             backgroundImage: `
               linear-gradient(rgba(0,255,255,0.1) 1px, transparent 1px),
               linear-gradient(90deg, rgba(0,255,255,0.1) 1px, transparent 1px)
             `,
             backgroundSize: '50px 50px'
           }} />

      {/* Subtle Glow Overlay */}
      <div className="absolute inset-0 bg-gradient-radial from-bw-accent/5 via-transparent to-transparent pointer-events-none z-10" />

      {/* Enhanced Particle Effects */}
      <div className="absolute inset-0 pointer-events-none z-5">
        {[...Array(12)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-bw-accent/40 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -30, 0],
              opacity: [0.2, 0.8, 0.2],
              scale: [1, 1.5, 1],
            }}
            transition={{
              duration: 4 + Math.random() * 3,
              repeat: Infinity,
              delay: Math.random() * 3,
            }}
          />
        ))}
      </div>

      {/* Larger Floating Elements */}
      <div className="absolute inset-0 pointer-events-none z-5">
        {[...Array(4)].map((_, i) => (
          <motion.div
            key={`large-${i}`}
            className="absolute w-2 h-2 bg-bw-accent/20 rounded-full"
            style={{
              left: `${20 + (i * 20)}%`,
              top: `${30 + (i * 15)}%`,
            }}
            animate={{
              y: [0, -20, 0],
              x: [0, 10, 0],
              opacity: [0.1, 0.4, 0.1],
            }}
            transition={{
              duration: 6 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 4,
            }}
          />
        ))}
      </div>

      {/* Corner Glow Effects */}
      <div className="absolute top-0 left-0 w-96 h-96 bg-gradient-radial from-bw-accent/10 via-transparent to-transparent pointer-events-none z-5" />
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-gradient-radial from-bw-accent/10 via-transparent to-transparent pointer-events-none z-5" />

      <div className="container-custom section-padding relative z-20 text-center flex flex-col items-center justify-center">
        {/* Artist Name - Enhanced Animation with Floating Effect */}
        <motion.span
          className="font-anton text-7xl md:text-9xl font-bold uppercase text-bw-white tracking-wider select-none mb-2 block"
          initial={{ opacity: 0, y: 60, scale: 0.8 }}
          animate={{ 
            opacity: 1, 
            scale: 1 
          }}
          transition={{ 
            duration: 1.2, 
            delay: 0.3, 
            type: 'spring', 
            stiffness: 60,
            damping: 15
          }}
          whileHover={{ 
            scale: 1.02,
            textShadow: "0 0 20px rgba(0,255,255,0.5)"
          }}
          style={{
            animation: "float 3s ease-in-out infinite"
          }}
        >
          FELIX FALKEN
        </motion.span>

        <motion.div
          initial={{ opacity: 0, y: 60 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2, delay: 0.8, ease: "easeOut" }}
          className="space-y-8"
        >
          {/* Main Title with Enhanced Motion */}
          <motion.h1
            initial={{ opacity: 0, scale: 0.9, y: 30 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            transition={{ duration: 1, delay: 1.1, ease: "easeOut" }}
            className="text-2xl md:text-4xl font-anton tracking-wider text-bw-white mb-6"
            whileHover={{ 
              scale: 1.05,
              textShadow: "0 0 15px rgba(0,255,255,0.3)"
            }}
          >
            ELECTRONIC SOUND ARCHITECT
          </motion.h1>

          {/* Enhanced CTA Buttons with Glow Effects */}
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 1.4, ease: "easeOut" }}
            className="flex flex-col sm:flex-row gap-6 justify-center items-center pt-4"
          >
            <motion.button
              whileHover={{ 
                scale: 1.08,
                boxShadow: "0 0 20px rgba(0,255,255,0.4)",
                borderColor: "#00FFFF"
              }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#music')}
              className="group relative overflow-hidden px-8 py-3 bg-bw-black border-2 border-bw-white text-bw-white font-bold uppercase tracking-wider transition-all duration-300 hover:border-bw-accent hover:text-bw-accent focus:outline-none focus:ring-2 focus:ring-bw-accent rounded-md"
            >
              {/* Button Glow Effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-bw-accent/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              <div className="relative flex items-center justify-center">
                <Play className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
                Listen Now
              </div>
            </motion.button>

            <motion.button
              whileHover={{ 
                scale: 1.08,
                boxShadow: "0 0 20px rgba(0,255,255,0.4)",
                borderColor: "#00FFFF"
              }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#collaborate')}
              className="group relative overflow-hidden px-8 py-3 bg-bw-black border-2 border-bw-white text-bw-white font-bold uppercase tracking-wider transition-all duration-300 hover:border-bw-accent hover:text-bw-accent focus:outline-none focus:ring-2 focus:ring-bw-accent rounded-md"
            >
              {/* Button Glow Effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-bw-accent/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              <div className="relative flex items-center justify-center">
                <Users className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
                Collaborate
              </div>
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
