import React from 'react';
import { motion } from 'framer-motion';
import { Play, Users } from 'lucide-react';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.querySelector(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center bg-bw-black">
      <div className="container-custom section-padding relative z-10 text-center flex flex-col items-center justify-center">
        {/* FF Logo above title, less margin, with pulsing motion */}
        <motion.div
          className="flex flex-col items-center"
          style={{ marginTop: '1.5rem', marginBottom: '1.5rem' }}
          initial={{ opacity: 0, scale: 0.9, y: -30 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2, type: 'spring', stiffness: 80 }}
        >
          <motion.img
            src={process.env.PUBLIC_URL + '/FF_logo.png'}
            alt="FF Logo"
            className="mx-auto w-56 md:w-80 h-auto object-contain select-none"
            animate={{ scale: [1, 1.12, 1] }}
            transition={{ repeat: Infinity, duration: 1.2, ease: 'easeInOut' }}
          />
        </motion.div>
        <motion.span
          className="font-anton text-5xl md:text-7xl font-bold uppercase text-bw-white tracking-wider select-none mb-2 block"
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5, type: 'spring', stiffness: 80 }}
        >
          FELIX FALKEN
        </motion.span>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.8 }}
          className="space-y-8"
        >
          {/* Main Title with subtle motion */}
          <motion.h1
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 1, delay: 1 }}
            className="text-2xl md:text-4xl font-anton tracking-wider text-bw-white mb-6"
            whileHover={{ scale: 1.04 }}
          >
            ELECTRONIC SOUND ARCHITECT
          </motion.h1>

          {/* CTA Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 1.2 }}
            className="flex flex-col sm:flex-row gap-6 justify-center items-center pt-4"
          >
            <motion.button
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#music')}
              className="bw-button group"
            >
              <Play className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
              Listen Now
            </motion.button>

            <motion.button
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => scrollToSection('#collaborate')}
              className="bw-button group"
            >
              <Users className="inline-block mr-2 w-5 h-5 group-hover:text-bw-accent transition-colors duration-150" />
              Collaborate
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
